import React from 'react'

const Notification = ({ message, notice }) => {
  if (message === null) {
    return null
  }
  console.log('notice in Notification.js', notice)
  const notificationStyle = {
    background: 'lightgrey',
    color: notice.type ==='ok' ? 'green' : 'red',
    fontSize: 20,
    borderStyle: 'solid',
    borderRadius: 5,
    padding: 5,
    marginBottom: 10
   }

  return (
    <div className="error" 

         style={notificationStyle}>
      {message}
    </div>
  )
}

export default Notification
