  //}

  return (
    <div className="formDiv">
      <h2>Create new</h2>

      <form onSubmit={addBlog}>
        <div>
          Title: <input
            id="title"
            name="title"
            type="text"
            //value={newTitle}
            //onChange={handleTitleChange}
          />
        </div>
        <div>
          Author: <input
            id="author"
            name="author"
            type="text"
            //value={newAuthor}
            //onChange={handleAuthorChange}
          />
        </div>
        <div>
          Url: <input
            id="url"
            name="url"
            type="text"
            //value={newUrl}
            //onChange={handleUrlChange}
          />
        </div>
        <button type="submit">create</button>
      </form>
    </div>
  )
}


export default BlogForm
