import React from 'react'

const Notification = ({ props }) => {
    console.log('notice in Notification.js', props.message)
  if (props === null) {
    return null
  }
  console.log('notice in Notification.js', props.message)
  const notificationStyle = {
    background: 'lightgrey',
    color: props.message.type ==='ok' ? 'green' : 'red',
    fontSize: 20,
    borderStyle: 'solid',
    borderRadius: 5,
    padding: 5,
    marginBottom: 10
   }

  return (
    <div className="error" 

         style={notificationStyle}>
      {props.message}
    </div>
  )
}

export default Notification
