import React from 'react'

const Notification = ({ notice }) => {
    console.log('notice in Notification.js', notice.message)
  if (notice.message === null) {
    return null
  }
  //console.log('notice in Notification.js', notice)
  const notificationStyle = {
    background: 'lightgrey',
    color: notice.message.type ==='ok' ? 'green' : 'red',
    fontSize: 20,
    borderStyle: 'solid',
    borderRadius: 5,
    padding: 5,
    marginBottom: 10
   }

  return (
    <div className="error" 

         style={notificationStyle}>
      {notice.message}
    </div>
  )
}

export default Notification
