import React from 'react'
import { useSelector } from 'react-redux'

//const users = useSelector(state => state.users)
//const dispatch = useDispatch()

const User = (user) => {
  const numberBlogs = user.blogs.length
  return(
    <div>
      {user.name} {numberBlogs}
    </div>
  )
}

const UserList = () => {
  const users = useSelector(state => state.users)
  return(
    <div>
      <h2>Users</h2>

      {users.map(user =>
        <li key={user.id}>
          <User />
        </li>)}
    </div>
  )
}

export default UserList
