      //  author: blog.author,
      //  url: blog.url,
      //  id: blog.id,
      //  user: blog.user.id
    //  }
  //  
    //  try {
      //  await blogService.update(likedBlog, blog.id)
      //  update()
    //  } catch (exception){
// 
    //  }   
  // }

  const toggle = () => {
    setVisibility(!visibility)
  }

  return (
    <div style={blogStyle}>
    <div style={hideWhenVisible}>
      {blog.title}
      {blog.author}
      <button onClick={toggle}>view</button>
    </div>
    <div style={showWhenVisibile}>
      {blog.title}
      {blog.author}
      <button onClick={toggle}>hide</button>
      <br/>
      {blog.url}
      <br/>
      {blog.likes}
      <button /* onClick={likeBlog} */> like</button>
      <br/>
      {blog.user.name}
    </div>
    </div>
  )
}

export default Blog
