import blogService from '../services/blogs'

const blogReducer = (state = [], action) => {
  console.log('state now: ', state)
  console.log('action now: ', action)
  switch (action.type) {
  case 'INIT_BLOGS':
    return action.data
  case 'NEW_BLOG':
    return [...state, action.data]
  case 'DELETE_BLOG': {
    return state.filter(blog => blog.id !== action.data)
  }
  case 'LIKE_BLOG': {
    const id = action.data.data.id
    return state.map(blog => blog.id !== id ? blog : action.data.data)
  }
  case 'NEW_COMMENT': {
    const id = action.id
    const comment = action.data
    //const commentedBlog = state.find(blog => blog.id === id)
    //console.log('commentedBlog in NEW_COMMENT: ', commentedBlog)
    console.log('id in NEW_COMMENT blogReducer: ', id)
    console.log('comment in NEW_COMMENT blogReducer: ', comment)

    return state.map(blog => blog.id !== id ? blog : {
      ...blog,
      comments: [...blog.comments, { comment }]
    })
    /* commentedBlog =
    return (...blog,
       comments: [...blog.comments, { comment }]) */
  }

  /* case 'ADD_COMMENT': {
    const commentedBlog = action.data
    console.log('comentedBlog in ADD_COMMENT: ', commentedBlog)
    return state.map(blog => blog.id !== commentedBlog ? blog : action.data)
  } */

  default:
    return state
  }
}

export const initializeBlogs = () => {
  return async dispatch => {
    const blogs = await blogService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      data: blogs,
    })
  }
}

export const createBlog = (title, author, url) => {
  return async dispatch => {
    const newBlog = await blogService.create(title, author, url)
    dispatch({
      type: 'NEW_BLOG',
      data: newBlog,
    })
  }
}

export const likeBlog = (blog) => {
  const changedBlog = {
    ...blog,
    likes: blog.likes + 1
  }
  return async dispatch => {
    const newObject = await blogService.update(blog.id, changedBlog)
    dispatch({
      type: 'LIKE_BLOG',
      data: newObject
    })
  }
}

export const addComment = (id, comment) => {
  console.log('id in addComment reducer: ', id)
  console.log('comment in addComment reducer: ', comment)
  return async dispatch => {
    const newComment = await blogService.addComment(id, comment)
    console.log('newComment in addComment reducer: ', newComment)
    dispatch({
      type: 'NEW_COMMENT',
      data: newComment, id
    })
  }
}

