import usersService from '../services/users'

const usersReducer = (state = null, action) => {
  switch (action.type) {
  case 'INIT_USER':
    return action.data

  default:
    return state
  }
}

export const initializeUsers = () => {
  return async dispatch => {
    const blogs = await usersService.getAll()
    dispatch({
      type: 'INIT_BLOGS',
      data: blogs,
    })
  }
}

export default usersReducer
