import React, {useState} from 'react'


const Blog = ({ blog }) => {

  const [visibility, setVisibility] = useState(false)
  
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const hideWhenVisible = { display: visibility ? 'none': ''}
  const showWhenVisibile = { display: visibility ? '' : 'none'}

  return (
    <div style={blogStyle}>
    {blog.title} {blog.author}
  </div>
  )
}

export default Blog
