import React, {useState} from 'react'
import blogService from '../services/blogs'


const Blog = ({ blogs, setBlogs, blog, notify }) => {

  const [visibility, setVisibility] = useState(false)
  
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const hideWhenVisible = { display: visibility ? 'none': ''}
  const showWhenVisibile = { display: visibility ? '' : 'none'}

  const likeBlog = async (id) => {

    const blog = blogs.find(b => b.id === id)
    const updateLike = { ...blog, likes: blog.likes + 1 }
    // const likedBlog = {
    //    title: blog.title,
    //    likes: blog.likes + 1,
    //    author: blog.author,
    //    url: blog.url,
    //    id: blog.id,
    //    user: blog.user.id
    //  }
    //  console.log('liked blog: ', likedBlog)
     
     try {
       await blogService
        .update(blog.id, likedBlog)
        setBlogs(blogs.map(b => b.id !== id ? blog : response))
        notify(`Like added to: ${blog.title}`, 'ok')
        } catch (exception){
        notify('Error! No like added')
     }   
  }

  const toggle = () => {
    setVisibility(!visibility)
  }

  return (
    <div style={blogStyle}>
      <div style={hideWhenVisible}>
        {blog.title}
        {blog.author}
        <button onClick={toggle}>view</button>
      </div>
      <div style={showWhenVisibile}>
        {blog.title}
        {blog.author}
        <button onClick={toggle}>hide</button>
        <br/>
        {blog.url}
        <br/>
        {blog.likes}
        <button onClick={likeBlog}>like</button>
        <br/>
        {blog.user.name}
      </div>
    </div>
  )
}

export default Blog
