import React from 'react'
import { useSelector } from 'react-redux'

//const users = useSelector(state => state.users)
//const dispatch = useDispatch()

/* const User = (user) => {
  console.log('user in User: ', user)
  const numberBlogs = user.blogs.length
  return(
    <div>
      {user.name} {numberBlogs}
    </div>
  )
} */

const UserList = () => {
  const users = useSelector(state => state.users)
  console.log('users in UserList: ', users)

  const userStyle = {
    paddingTop: 1,
    paddingLeft: 0,
    marginBottom: 1
  }

  /*  const headerStyle = {
    marginBottom: .5
  } */

  return(
    <div>
      <h2>Users</h2>
      <div>
        <div>
          <ul style={userStyle}>
            <h4> </h4>
            {users.map(user =>
              <li key={user.id} style={userStyle}>
                {user.name}
              </li>
            )}
          </ul>
        </div>
        <div>
          <ul style={userStyle}>
            <h6>blogs created</h6>
            {users.map(user =>
              <li key={user.id} style={userStyle}>
                {user.blogs.length}
              </li>)}
          </ul>
        </div>
      </div>
    </div>
  )
}

export default UserList
