import React from 'react'
import { useDispatch } from 'react-redux'

import { likeBlog, deleteBlog, addComment } from '../reducers/blogReducer'
import { setNotification } from '../reducers/notificationReducer'

import { useHistory } from 'react-router-dom'

const Blog = ({ blog, loggedUser }) => {

  const dispatch = useDispatch()
  const history = useHistory()

  // Allow blogs to be deleted only if blog post created by user
  const showDeleteButton = () => {

    if (loggedUser.username === blog.user['username']) {
      return (
        <button onClick={() => handleDeleteBlog(blog.id)}>Remove Blog</button>
      )
    }
  }

  const handleDeleteBlog = (id) => {
    if (window.confirm(`Remove: ${blog.title} By: ${blog.author}?`))
      try {
        dispatch(deleteBlog(id))
        history.push('/')
        dispatch(setNotification(`${blog.title} by ${blog.author} was successfully deleted!`, 'ok'))
      } catch(exception){
        dispatch(setNotification('Error!'))
      }
  }

  const handleLike = async () => {
    try {
      dispatch(likeBlog(blog))
      dispatch(setNotification(`Like added to: ${blog.title}`, 'ok'))
    } catch (exception){
      dispatch(setNotification('Error!', 5))
    }
  }

  const handleComment = async (event) => {
    event.preventDefault()
    const comment = event.target.comment.value
    event.target.comment.value = ''
    dispatch(addComment(blog.id, comment))
  }

  if (!blog) {
    return null
  }

  const buttonStyle = {
    marginLeft: 3,
    padding: (0, 2, 0, 2),
    borderRadius: 6,
  }

  return(
    <div>
      <div>
        <h2>{blog.title} {blog.author}</h2>
      </div>
      <div>
        {blog.url}
        <br />
        {blog.likes} Likes
        <button onClick={handleLike} style={buttonStyle}>like</button>
        <br />
        added by: {blog.user.name}
      </div>
      <div>
        <br />
        <br />
        <h5>comments</h5>
        <br />
        <div>
          <form onSubmit={handleComment}>
            <input
              name="comment"
            />
            <button type="submit">add comment</button>
          </form>
        </div>
        <br />
        <ul>
          {blog.comments.map(comment =>
            <li key={comment.id}>{comment.comment}</li>)}
        </ul>
        <br />
        {showDeleteButton()}
      </div>
    </div>
  )
}

export default Blog
